import React, { Component } from 'react';
import TodoTaker from './TodoTaker';
import TodoList from './TodoList';
import ToolbarComponent from './ToolbarComponent';
import MyDrawer from './MyDrawer';


class MainComponent extends Component{
    constructor(props){
        super(props);
        this.state={
            title:"Google Keep",
            tasks: []
        }

        this.handleParent=this.handleParent.bind(this)
        this.toDoAppremoveTask=this.toDoAppremoveTask.bind(this)
    }

    //this method is used as a tool to communicate with the child. Parent component provides means for communication
    handleParent(task){
        const newState=[{
            id: Math.random()*3465768768,
            value:task
        }];

        //this is an async callback method. Printing tasks on the next line of this line will print 1 step later for each value.
        this.setState((currState)=>({
            tasks:currState.tasks.concat(newState)
        }));
    }

    toDoAppremoveTask(id){
        console.log(id)
        this.state.tasks = this.state.tasks.filter((item) => item.id != id);
        this.setState((currState)=>({
            tasks:currState.tasks
        }));
    }

    render(){
        //we can pass props to these components by using the sates of this component
        return (<div><ToolbarComponent title={this.state.title}/> 
        <MyDrawer />
        <div className={classes.todoTaker}>
        <TodoTaker handleParent={this.handleParent}/>
              </div>
        <TodoList tasks={this.state.tasks} toDoAppremoveTask={this.toDoAppremoveTask}/></div>)
    } 
}

const styles = theme => ({
    root: {
      width: '100%',
    },
    grow: {
      flexGrow: 1,
    },
    menuButton: {
      marginLeft: -12,
      marginRight: 20,
    },
    title: {
      display: 'none',
      [theme.breakpoints.up('sm')]: {
        display: 'block',
      },
    },
    search: {
      position: 'relative',
      borderRadius: theme.shape.borderRadius,
      backgroundColor: fade(theme.palette.common.white, 0.15),
      '&:hover': {
        backgroundColor: fade(theme.palette.common.white, 0.25),
      },
      marginRight: theme.spacing.unit * 2,
      marginLeft:0,
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing.unit * 20,
        width: 700,
      },
    },
    searchIcon: {
      width: theme.spacing.unit * 9,
      height: '100%',
      position: 'absolute',
      pointerEvents: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    inputRoot: {
      color: 'inherit',
      width: '100%',
    },
    inputInput: {
      paddingTop: theme.spacing.unit,
      paddingRight: theme.spacing.unit,
      paddingBottom: theme.spacing.unit,
      paddingLeft: theme.spacing.unit * 10,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('md')]: {
        width: 200,
      },
    },
    sectionDesktop: {
      display: 'none',
      [theme.breakpoints.up('md')]: {
        display: 'flex',
      },
    },
    sectionMobile: {
      display: 'flex',
      [theme.breakpoints.up('md')]: {
        display: 'none',
      },
    },
  });


export default MainComponent;
