import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';

import '../styles/TodoTaker.css'





class TodoTaker extends Component{
    constructor(props){
        super(props);
        this.state={
            value:""
        }

        //binding is important if u want to change any property using this function
        this.handleChange=this.handleChange.bind(this)
        this.handleSubmit=this.handleSubmit.bind(this)
    }

    handleChange1(e){

        this.setState({
            value:e.target.value
        });
    }
    handleChange2(e){

        this.setState({
            value:e.target.value
        });
    }

    handleSubmit(e){
        e.preventDefault();
    this.props.handleParent(this.state.value);
    this.state.value="";
    }

    render(){
        return(<div><Card >
            <CardContent>
            <TextField placeholder="Take your task..." onChange={this.handleChange1} value={this.state.value}/> 
            <TextField placeholder="Take your task..." onChange={this.handleChange2} value={this.state.value}/> 

            </CardContent>
            <CardActions>
              <Button className='saveButton' onClick={this.handleSubmit}>Add Task</Button>
            </CardActions>
          </Card></div>)
    }
}


  export default TodoTaker;
