//     content: newState[0].content,
//   }})
//});

var url = 'http://localhost:3000/users/ashish.basak';
var data = {"id":"ashish.basak@gmail.com",
  "notes":
    this.state.tasks};

fetch(url, {
  method: 'PUT', // or 'PUT'
  body: JSON.stringify(data), // data can be `string` or {object}!
  headers:{
    'Content-Type': 'application/json'
  }
}).then(res => res.json())
.then(response => console.log('Success:', JSON.stringify(response)))
.catch(error => console.error('Error:', error));

}

    }

    toDoAppremoveTask(id){
        console.log(id)
        this.state.tasks = this.state.tasks.filter((item) => item.id != id);
        this.setState((currState)=>({
            tasks:currState.tasks
        }));
    }

    render(){

        //we can pass props to these components by using the sates of this component
        return (<div><ToolbarComponent title={this.state.title}/> 
                    <MyDrawer />
                    <div className='container'>
                        <div className='todotaker'>
                            <TodoTaker handleParent={this.handleParent}/>
                        </div>
                        <TodoList tasks={this.state.tasks} toDoAppremoveTask={this.toDoAppremoveTask}/>
                    </div>
                </div>)
    } 
}


export default (MainComponent);
