import React, { Component } from 'react';
import check from '../routes/CheckUser'
import MainComponent from './MainComponent';


class LoginComponent extends Component{
    constructor(props){
        super(props);
        this.state={
            email:"",
            password:"",
            loggedIn:false
        }
        this.handleEmail=this.handleEmail.bind(this);
        this.handlePassword=this.handlePassword.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
    }

    handleEmail(e){
        this.setState({
            email:e.target.value
        });
    }

    handlePassword(e){
        this.setState({
            password:e.target.value
        });
    }
    handleSubmit(){

        check(this.state.email).then((response)=>{
            if(this.state.password==response.password){
                alert("Login Success!!!");
                this.state.loggedIn=true;
            }
            else{
                alert("Login Failed!");

            }
    }
);
}


    render() {
        if(this.state.loggedIn){
        return (
            <div>
                <div id="login">
                    <input onChange={this.handleEmail} type="email" id="email" placeholder="Email" /><br/>
                    <input onChange={this.handlePassword} type="password" id="password" placeholder="Password" /><br/>
                    <button id="send" onClick={this.handleSubmit} >Login</button>
                </div>
            </div>

        )
        }
        else{
            return(<MainComponent/>)
        }
    }
}

export default LoginComponent;
